      generateRandomNumber();
      generateRandomNumber();
    }

    function generateRandomNumber() {
      let emptyCells = [];
      for (let i = 0; i < 16; i++) {
        if (board[Math.floor(i / 4)][i % 4] === 0) {
          emptyCells.push(i);
        }
      }
      let randomIndex = Math.floor(Math.random() * emptyCells.length);
      let cellIndex = emptyCells[randomIndex];
      if (emptyCells.length > 0) {
        board[Math.floor(cellIndex / 4)][cellIndex % 4] = Math.random() < 0.9 ? 2 : 4;
        updateCell(cellIndex);
      }
    }

    function updateCell(index) {
      const cell = cells[index];
      const value = board[Math.floor(index / 4)][index % 4];
      cell.textContent = value === 0 ? '' : value;
      cell.classList.remove('empty', '2', '4', '8', '16', '32', '64', '128', '256', '512', '1024', '2048');
      cell.classList.add(value === 0 ? 'empty' : `cell ${value}`);
    }

    function moveLeft() {
      for (let i = 0; i < 4; i++) {
        for (let j = 1; j < 4; j++) {
          let k = j;
          while (k > 0 && board[i][k - 1] === 0) {
            if (board[i][j] !== 0) {
              board[i][k - 1] = board[i][k];
              board[i][k] = 0;
              k--;
            }
            if (k > 0 && board[i][k - 1] === board[i][k]) {
              board[i][k] = 0;
              board[i][k - 1] *= 2;
            }
          }
        }
      }
    }

    function moveRight() {
      for (let i = 0; i < 4; i++) {
        for (let j = 2; j >= 0; j--) {
          if (board[i][j] !== 0) {
            let k = j;
            while (k < 3 && board[i][k + 1] === 0) {
              board[i][k + 1] = board[i][k];
              board[i][k] = 0;
              k++;
            }
            if (k < 3 && board[i][k + 1] === board[i][k]) {
              board[i][k + 1] *= 2;
              board[i][k] = 0;
            }
          }
        }
      }
    }

    function moveUp() {
      for (let j = 0; j < 4; j++) {
        for (let i = 1; i < 4; i++) {
          if (board[i][j] !== 0) {
            let k = i;
            while (k > 0 && board[k - 1][j] === 0) {
              board[k - 1][j] = board[k][j];
              board[k][j] = 0;
              k--;
            }
            if (k > 0 && board[k - 1][j] === board[k][j]) {
              board[k - 1][j] *= 2;
              board[k][j] = 0;
            }
          }
        }
      }
    }

    function moveDown() {
      for (let i = 2; i >= 0; i--) {
        for (let j = 0; j < 4; j++) {
          if (board[i][j] !== 0) {
            let k = i;
            while (k < 3 && board[k + 1][j] === 0) {
              board[k + 1][j] = board[k][j];
              board[k][j] = 0;
              k++;
            }
            if (k < 3 && board[k + 1][j] === board[k][j]) {
              board[k + 1][j] *= 2;
              board[k][j] = 0;
            }
          }
        }
      }
    }

    document.addEventListener('keydown', (event) => {
      switch (event.key) {
        case 'ArrowLeft':
          moveLeft();
          break;
        case 'ArrowRight':
          moveRight();
          break;
        case 'ArrowUp':
          moveUp();
          break;
        case 'ArrowDown':
          moveDown();
          break;
      }
      updateBoard();
      generateRandomNumber();
    });

    function updateBoard() {
      for (let i = 0; i < 16; i++) {
        updateCell(i);
      }
    }

    initGame();
  </script>
