 board[i][k + 1] *= 2;
 board[i][k] = 0;
 }
 }
 }
 }
 }

 function moveUp() {
 for (let j = 0; j < 4; j++) {
 for (let i = 1; i < 4; i++) {
 if (board[i][j] !== 0) {
