const fs = require('fs');
const path = require('path');

const dataPath = path.resolve(__dirname, 'data', 'users.json');

// Fungsi untuk membaca data pengguna
function getUserData(userId) {
 if (!fs.existsSync(dataPath)) {
 fs.writeFileSync(dataPath, JSON.stringify({}));
 }
 let rawData = fs.readFileSync(dataPath);
 let users = JSON.parse(rawData);
 if (!users[userId]) {
 users[userId] = {
 username: `User${userId}`, // Anda dapat mengganti dengan nama pengguna sebenarnya jika tersedia
 coin: 0,
 hp: 100,
 inventory: {},
 items: [],
 armor: false
 };
 fs.writeFileSync(dataPath, JSON.stringify(users, null, 2));
 }
 return users[userId];
}

// Fungsi untuk menyimpan data pengguna
function setUserData(userId, data) {
 let rawData = fs.readFileSync(dataPath);
 let users = JSON.parse(rawData);
 users[userId] = data;
 fs.writeFileSync(dataPath, JSON.stringify(users, null, 2));
}

module.exports = {
 config: {
 name: "rpg",
 version: "1.0.0",
 author: "Bernando",
 countDown: 5,
 role: 0,
 description: "RPG game",
 category: "Game",
 guide: "Use `rpg` to see menu or `rpg <subcommand>` for specific actions."
 },
 onStart: async function({ api, args, message, usersData, event }) {
 const subcommand = args[0];

 if (!subcommand) {
 return showMenu(api, event);
 }

 switch(subcommand.toLowerCase()) {
 case 'menu':
 return showMenu(api, event);
 case 'status':
 return status(api, event);
 case 'survival':
 return survival(api, event);
 case 'healing':
 return healing(api, event);
 case 'mining':
 return mining(api, event);
 case 'shop':
 return shop(api, event, args.slice(1));
 case 'dungeon':
 return dungeon(api, event);
 case 'use':
 return useItem(api, event, args.slice(1));
 default:
 return api.sendMessage("Subcommand tidak ditemukan! Gunakan `rpg menu` untuk melihat menu.", event.threadID, event.messageID);
 }
 }
};

// Fungsi untuk menampilkan menu RPG
async function showMenu(api, event) {
 const menu = `
üéÆ *RPG Menu*
1. \`rpg status\` - Lihat statusmu
2. \`rpg survival\` - Aktifitas survival
3. \`rpg healing\` - Sembuhkan diri
4. \`rpg mining\` - Menambang
5. \`rpg shop\` - Buka toko
6. \`rpg dungeon\` - Masuk ke dungeon
7. \`rpg use <item>\` - Gunakan item (armor, medkit)

*Ketik* \`rpg <subcommand>\` *untuk memulai.*
 `;
 return api.sendMessage(menu, event.threadID, event.messageID);
}

// Fungsi untuk menampilkan status pengguna
async function status(api, event) {
 const userId = event.senderID;
 let user = getUserData(userId);

 const items = user.items.length > 0 ? user.items.join(', ') : 'Tidak ada';
 const inventoryItems = Object.keys(user.inventory).length > 0 ? Object.keys(user.inventory).map(item => `${item} (${user.inventory[item]})`).join(', ') : 'Tidak ada';

 const statusMessage = `
üìã **Status Kamu**

**Username:** ${user.username}
**Coin:** ¬¢${user.coin.toLocaleString()}
**HP:** ${user.hp}/100
**Items:** ${items}
**Inventory:** ${inventoryItems}
**Armor:** ${user.armor ? 'Aktif üõ°' : 'Nonaktif'}
 `;
 return api.sendMessage(statusMessage, event.threadID, event.messageID);
}

// Fungsi untuk aksi Survival
async function survival(api, event) {
 const userId = event.senderID;
 let user = getUserData(userId);

 // Simulasi mendapatkan barang bekas
 const possibleItems = ['Botol', 'Karet', 'Logam', 'Kayu'];
 const gotItem = possibleItems[Math.floor(Math.random() * possibleItems.length)];
 user.inventory[gotItem] = (user.inventory[gotItem] || 0) + 1;

 // Otomatis menjual barang
 const itemPrices = {
 'Botol': 10,
 'Karet': 20,
 'Logam': 30,
 'Kayu': 15
 };
 const earned = itemPrices[gotItem];

 user.coin += earned;

 // Status sekarat
 const sekaratChance = Math.random();
 if (sekaratChance < 0.2) { // 20% chance
 user.hp -= 50;
 if (user.hp <= 0) {
 user.hp = 50; // Set ke sekarat, tapi tidak mati
 await api.sendMessage("Kamu pulang dalam keadaan sekarat! Gunakan `rpg healing` untuk sembuh.", event.threadID, event.messageID);
 }
 }

 setUserData(userId, user);

 return api.sendMessage(`Kamu mendapatkan *${gotItem}* dan menjualnya seharga ¬¢${earned}. \nUangmu sekarang: ¬¢${user.coin}. \nHP: ${user.hp}/100`, event.threadID, event.messageID);
}

// Fungsi untuk aksi Healing
async function healing(api, event) {
 const userId = event.senderID;
 let user = getUserData(userId);

 if (user.hp >= 100) {
 return api.sendMessage("HP-mu sudah penuh!", event.threadID, event.messageID);
 }

 const medkitCount = user.inventory['Medkit'] || 0;
 if (medkitCount < 1) {
 return api.sendMessage("Kamu tidak memiliki Medkit! Beli di toko dengan `rpg shop`.", event.threadID, event.messageID);
 }

 // Gunakan medkit
 user.hp += 50;
 if (user.hp > 100) user.hp = 100;
 user.inventory['Medkit'] -= 1;

 setUserData(userId, user);

 return api.sendMessage(`Kamu menggunakan Medkit dan menyembuhkan diri. HP sekarang: ${user.hp}/100`, event.threadID, event.messageID);
}

// Fungsi untuk aksi Mining
async function mining(api, event) {
