 let user = getUserData(userId);

 if (!user.items.includes('Pickaxe')) {
 return api.sendMessage("Kamu perlu membeli Pickaxe di toko sebelum melakukan mining! Gunakan `rpg shop`.", event.threadID, event.messageID);
