}

// Fungsi untuk aksi Shop
async function shop(api, event, args) {
 const userId = event.senderID;
 const subcommand = args[0];

 // Definisikan item yang dijual
 const shopItems = {
 'pickaxe': { price: 500, name: 'Pickaxe' },
 'sword': { price: 1000, name: 'Sword' },
 'shield': { price: 800, name: 'Shield' },
 'medkit': { price: 300, name: 'Medkit' }
 };

 if (!subcommand) {
 // Tampilkan daftar toko
 let shopList = "- *Toko RPG* -\n";
 for (let item in shopItems) {
 shopList += `‚Ä¢ ${item}: ¬¢${shopItems[item].price}\n`;
 }
 shopList += `\nGunakan \`rpg shop <item>\` untuk membeli item.`;
 return api.sendMessage(shopList, event.threadID, event.messageID);
 }

 const itemName = subcommand.toLowerCase();
 const item = shopItems[itemName];

 if (!item) {
 return api.sendMessage("Item tidak tersedia di toko. Gunakan `rpg shop` untuk melihat daftar item.", event.threadID, event.messageID);
 }

 let user = getUserData(userId);

 if (user.coin < item.price) {
 return api.sendMessage(`Kamu tidak memiliki cukup uang untuk membeli ${item.name}.`, event.threadID, event.messageID);
 }

 user.coin -= item.price;

 // Untuk tool seperti pickaxe, sword, shield
 if (['pickaxe', 'sword', 'shield'].includes(itemName)) {
 if (!user.items.includes(item.name)) {
 user.items.push(item.name);
 await api.sendMessage(`Kamu membeli *${item.name}*!`, event.threadID, event.messageID);
 } else {
 await api.sendMessage(`Kamu sudah memiliki *${item.name}*.`, event.threadID, event.messageID);
 }
 } else {
 // Untuk consumable seperti medkit
 user.inventory[item.name] = (user.inventory[item.name] || 0) + 1;
 await api.sendMessage(`Kamu membeli *${item.name}*.`, event.threadID, event.messageID);
 }

 setUserData(userId, user);
}

// Fungsi untuk aksi Use
async function useItem(api, event, args) {
 const userId = event.senderID;
 let user = getUserData(userId);

 const itemToUse = args[0]?.toLowerCase();

 if (!itemToUse) {
 return api.sendMessage("Gunakan `rpg use <item>`. Item yang bisa digunakan: armor, medkit.", event.threadID, event.messageID);
 }

 if (itemToUse === 'armor') {
 if (user.armor) {
 // Jika armor sudah aktif, nonaktifkan
 user.armor = false;
 setUserData(userId, user);
 return api.sendMessage("Kamu menonaktifkan armor.", event.threadID, event.messageID);
 } else {
 user.armor = true;
 setUserData(userId, user);
 return api.sendMessage("Kamu mengaktifkan armor. Armor akan mengurangi damage yang kamu terima.", event.threadID, event.messageID);
 }
 } else if (itemToUse === 'medkit') {
 if (user.inventory['Medkit'] && user.inventory['Medkit'] > 0) {
 user.hp += 50;
 if (user.hp > 100) user.hp = 100;
 user.inventory['Medkit'] -= 1;
 setUserData(userId, user);
 return api.sendMessage(`Kamu menggunakan Medkit dan menyembuhkan diri. HP sekarang: ${user.hp}/100`, event.threadID, event.messageID);
 } else {
 return api.sendMessage("Kamu tidak memiliki Medkit!", event.threadID, event.messageID);
 }
 } else {
 return api.sendMessage("Item tidak dapat digunakan. Item yang bisa digunakan: armor, medkit.", event.threadID, event.messageID);
 }
}

// Fungsi untuk aksi Dungeon
async function dungeon(api, event) {
 const userId = event.senderID;
 let user = getUserData(userId);

 // Tentukan level dungeon
 const dungeonLevel = Math.floor(Math.random() * 10) + 1;

 // Tentukan monster
 const monsterHealth = dungeonLevel * 20;
 let monsterCurrentHealth = monsterHealth;

 await api.sendMessage(`üè∞ Kamu memasuki dungeon level ${dungeonLevel}! Monster memiliki ${monsterCurrentHealth} HP.`, event.threadID, event.messageID);

 // Mulai pertarungan
 while (monsterCurrentHealth > 0 && user.hp > 0) {
 // Tampilkan pilihan aksi
 await api.sendMessage("Pilih aksi: `serang`, `bertahan`, `healing`, atau `kabur`.\n*Balas dengan salah satu pilihan di atas.*", event.threadID, event.messageID);

 try {
 const action = await getUserAction(api, event);

 let damageToMonster = 0;
 let damageToUser = 0;

 // Tentukan tindakan pengguna
 if (action === 'serang') {
 // Serangan berdasarkan tingkat
 damageToMonster = Math.floor(Math.random() * 20) + 10;
 monsterCurrentHealth -= damageToMonster;
 await api.sendMessage(`üî´ Kamu menyerang monster dan memberikan ${damageToMonster} damage!`, event.threadID, event.messageID);
 } else if (action === 'bertahan') {
 await api.sendMessage("üõ° Kamu memilih untuk bertahan. Mengurangi damage yang diterima pada serangan berikutnya.", event.threadID, event.messageID);
 // Implementasi logika bertahan jika diperlukan
 // Misalnya, mengurangi damage yang diterima monster
 } else if (action === 'healing' || action === 'pulihkan') {
 if (user.inventory['Medkit'] && user.inventory['Medkit'] > 0) {
 user.hp += 30;
 if (user.hp > 100) user.hp = 100;
 user.inventory['Medkit'] -= 1;
 await api.sendMessage(`üíâ Kamu menggunakan Medkit dan menyembuhkan diri. HP sekarang: ${user.hp}/100`, event.threadID, event.messageID);
 } else {
 await api.sendMessage("‚ùå Kamu tidak memiliki Medkit!", event.threadID, event.messageID);
 }
 } else if (action === 'kabur') {
 await api.sendMessage("üèÉ‚Äç‚ôÇ Kamu berhasil kabur dari dungeon.", event.threadID, event.messageID);
 setUserData(userId, user);
 return;
 } else {
 await api.sendMessage("‚ö† Aksi tidak valid. Silakan coba lagi.", event.threadID, event.messageID);
 continue;
 }

 // Serangan monster jika masih hidup
 if (monsterCurrentHealth > 0) {
 const monsterAction = Math.random() < 0.5 ? 'serang' : 'bertahan';
 if (monsterAction === 'serang') {
 damageToUser = dungeonLevel * 5;
 if (user.armor) {
 damageToUser = Math.floor(damageToUser / 2);
 }
 user.hp -= damageToUser;
 await api.sendMessage(`üëæ Monster menyerang dan memberikan ${damageToUser} damage!`, event.threadID, event.messageID);
 } else {
 await api.sendMessage("üëæ Monster memilih untuk bertahan.", event.threadID, event.messageID);
 // Implementasi logika bertahan monster jika diperlukan
 }

 if (user.hp <= 0) {
 await api.sendMessage("üíÄ Kamu telah sekarat! Gunakan `rpg healing` untuk menyembuhkannya atau bayar ¬¢1000 untuk hidup kembali.", event.threadID, event.messageID);
 // Implementasikan opsi untuk menyembuh atau bayar jika diperlukan
 setUserData(userId, user);
 return;
 }
 }

 // Cek jika monster sudah mati
 if (monsterCurrentHealth <= 0) {
 await api.sendMessage(`üéâ Kamu mengalahkan monster level ${dungeonLevel}!`, event.threadID, event.messageID);
 const reward = dungeonLevel * 100;
 user.coin += reward;
 await api.sendMessage(`ü™ô Kamu mendapatkan ¬¢${reward}! Uangmu sekarang: ¬¢${user.coin}`, event.threadID, event.messageID);

 if (dungeonLevel === 10 && !user.armor) {
 user.armor = true;
 await api.sendMessage("üõ° Kamu berhasil melewati dungeon level 10 dan mendapatkan Iron Armor! Damage yang diterima sekarang berkurang.", event.threadID, event.messageID);
 }

 break;
 }

 setUserData(userId, user);
 } catch (err) {
 await api.sendMessage("‚è∞ Waktu telah habis untuk memilih aksi. Dungeon dibatalkan.", event.threadID, event.messageID);
 return;
 }
 }

 setUserData(userId, user);
}

// Fungsi untuk mendapatkan aksi pengguna dengan timeout
function getUserAction(api, event) {
 return new Promise((resolve, reject) => {
 const filter = m => m.senderID === event.senderID;
 // Tergantung pada library yang digunakan, sesuaikan event listener
 // Berikut adalah contoh dengan menggunakan Discord.js
 const collector = api.createMessageCollector(event.threadID, { filter, max: 1, time: 60000 });

 collector.on('collect', message => {
 resolve(message.body.toLowerCase());
 });

 collector.on('end', collected => {
 if (collected.size === 0) {
 reject(new Error('timeout'));
 }
 });
 });
}
